CREATE TABLE "friends" ("uid" VARCHAR NOT NULL, "friend_uid" VARCHAR NOT NULL, PRIMARY KEY ("uid", "friend_uid"));
CREATE TABLE "groups" ("id" VARCHAR NOT NULL, "name" VARCHAR, "created_at" TIMESTAMPTZ NOT NULL DEFAULT current_timestamp, "updated_at" TIMESTAMPTZ NOT NULL DEFAULT current_timestamp, "deleted_at" TIMESTAMPTZ, PRIMARY KEY ("id"));
CREATE TABLE "group_users" ("uid" VARCHAR NOT NULL, "group_id" VARCHAR NOT NULL, "owner" BOOLEAN NOT NULL, PRIMARY KEY ("uid", "group_id"));
CREATE TABLE "events" ("id" VARCHAR NOT NULL, "name" VARCHAR, "created_by" VARCHAR, "paid_by" VARCHAR NOT NULL, "amount" BIGINT NOT NULL, "group_id" VARCHAR, "evented_at" TIMESTAMPTZ NOT NULL, "created_at" TIMESTAMPTZ NOT NULL DEFAULT current_timestamp, "updated_at" TIMESTAMPTZ NOT NULL DEFAULT current_timestamp, "deleted_at" TIMESTAMPTZ, PRIMARY KEY ("id"));
CREATE TABLE "payments" ("id" VARCHAR NOT NULL, "evented_by" VARCHAR NOT NULL, "paid_by" VARCHAR NOT NULL, "paid_to" VARCHAR NOT NULL, "paid_at" TIMESTAMPTZ NOT NULL, "amount" BIGINT NOT NULL, "created_at" TIMESTAMPTZ NOT NULL DEFAULT current_timestamp, "updated_at" TIMESTAMPTZ NOT NULL DEFAULT current_timestamp, "deleted_at" TIMESTAMPTZ, PRIMARY KEY ("id"));
